Scripts.OnDragStart:
if getglobal('TWTMainLockButton'):GetText() == 'L' then
this:StartMoving();
this.isMoving = true;
end

Scripts.OnEnter:
GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
if this:GetText() == 'L' then
GameTooltip:AddLine('Lock Window')
else
GameTooltip:AddLine('Unlock Window')
end
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Glow');
GameTooltip:AddLine('Shows a colored aura ranging from Green/Yellow/Red on \nthe Default UI\'s unit frame.', 1, 1,
1);
GameTooltip:AddLine('The color of the aura is based on your current threat threshold:', 1, 1, 1);
GameTooltip:AddLine(' green - yellow for 0% - 49%', 1, 1, 1);
GameTooltip:AddLine(' yellow - red for 50% - 100%', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Full Screen Glow');
GameTooltip:AddLine('Red glowing screen edges when your threat is above 80%.', 1, 1, 1);
GameTooltip:AddLine('This feature is disabled in Tank Mode.', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Aggro Warning Sound');
GameTooltip:AddLine('Plays a warning sound when you reach 99% threat.', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Percentages');
GameTooltip:AddLine('Shows threat percentages on the default target frame.', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Combat');
GameTooltip:AddLine('Automatically shows the window when combat begins.', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Combat');
GameTooltip:AddLine('Automatically hides the window when combat begins.', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Tank Mode');
GameTooltip:AddLine('Extra window showing creatures you are tanking. ', 1, 1, 1);
GameTooltip:AddLine('Disables Full Screen Glow effect. ', 1, 1, 1);
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Show TPS/Threat/% Label Row');
GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Show TPS Column');

GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Show Threat Column');

GameTooltip:Show();

GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_RIGHT", -(this:GetWidth() / 2) + 5, 0);
GameTooltip:AddLine('Show Percent Column');

GameTooltip:Show();
